mapper.select:
        select * from(select rownum n, a.* from(select * from plan p 

            where (plan_title like '%' || #{keyword} || '%' or (select distinct  k.code  from plan p , korea k where p.korea_code = k.code 
			and k.region like '%' || #{keyword} || '%') = p.korea_code)


            AND p.korea_code = #{region}


            order by ${orderColumn} desc

        ) a) where n between #{start} and #{end}

    select plan_no, plan_title, plan_img, member_id, korea_code
	from (select rownum n, a.* 
	from (select * from plan where korea_code = #{region} order by ${sort_col} desc) a)
	where n between #{start} and #{end}

select count(*) from plan
where (plan_title like '%' || #{keyword} || '%' or (select distinct k.code from plan p , korea k where p.korea_code = k.code and k.region like '%' || #{keyword} || '%') = korea_code)
AND korea_code = #{region}

select p.* from plan p, liked l
where p.plan_no = l.NO
and l.type = 'plan' and l.member_id = #{id};
